mapper.select:
SELECT BOARD_NO, WRITER, TITLE, CONTENT, HIT, IP, CREATED, MODIFIED
  FROM BOARD
 WHERE BOARD_NO = #{boardNo}

mapper.update:
UPDATE BOARD
   SET HIT = HIT + 1
 WHERE BOARD_NO = #{boardNo}

UPDATE BOARD
   SET TITLE = #{title}, CONTENT = #{content}, MODIFIED = SYSDATE
 WHERE BOARD_NO = #{boardNo}
